import{_ as a,c as e,o as t,a4 as o}from"./chunks/framework.4aTu-Nia.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/02 技术/供应链技术/单车调度.md","filePath":"docs/02 技术/供应链技术/单车调度.md"}'),r={name:"docs/02 技术/供应链技术/单车调度.md"},s=o('<p><strong>摘要：</strong> 运营商自主调度能够有效解决共享单车供需失衡问题，使车辆分布与用户需求分布更加吻合，提高系统的运转效率和用户满意度。共享单车调度是允许存在局部路径重复的多旅行商问题，引入动态路径规划优化模型对共享单车调度问题进行描述，主要考虑如何以最少调度车数量、最短的总运输里程实现总调度成本最小，并设计遗传算法进行求解。最后以北京地区为例，采用300多万条摩拜单车订单数据进行实证分析，多次实验后得出最优调度方案，并确定最少调度车数量和最小总调度成本，表明了模型和算法的有效性。</p><h2 id="指标" tabindex="-1">指标 <a class="header-anchor" href="#指标" aria-label="Permalink to &quot;指标&quot;">​</a></h2><h3 id="车辆利用率" tabindex="-1">车辆利用率 <a class="header-anchor" href="#车辆利用率" aria-label="Permalink to &quot;车辆利用率&quot;">​</a></h3><p>平均一辆单车被骑行次数/时长 无车可用比例 车辆堆积比例</p><h3 id="怎么判断停满了" tabindex="-1">怎么判断停满了？ <a class="header-anchor" href="#怎么判断停满了" aria-label="Permalink to &quot;怎么判断停满了？&quot;">​</a></h3><p>单车数量/面积</p>',6),_=[s];function n(c,d,i,h,l,p){return t(),e("div",null,_)}const u=a(r,[["render",n]]);export{f as __pageData,u as default};
