import{_ as e,c as s,o as n,a4 as o}from"./chunks/framework.4aTu-Nia.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/02 技术/机器学习/特征工程/文本特征工程.md","filePath":"docs/02 技术/机器学习/特征工程/文本特征工程.md"}'),a={name:"docs/02 技术/机器学习/特征工程/文本特征工程.md"},c=o(`<p>One-Hot、Label Encoding、Word 2 Vec</p><ul><li><p><strong>独热编码（One-Hot Encoding）</strong>：</p><ul><li><code>[&#39;红&#39;, &#39;绿&#39;, &#39;蓝&#39;]</code> → <code>[[1,0,0], [0,1,0], [0,0,1]]</code></li><li>适用于 <code>类别数量较少</code> 的变量，如 <code>性别、颜色</code>。</li></ul></li><li><p><strong>标签编码（Label Encoding）</strong>：</p><ul><li><code>[&#39;红&#39;, &#39;绿&#39;, &#39;蓝&#39;]</code> → <code>[0,1,2]</code></li><li>适用于 <code>类别有序</code> 变量，如 <code>教育水平（小学 &lt; 初中 &lt; 高中 &lt; 大学）</code>。</li></ul></li><li><p><strong>频次编码（Frequency Encoding）</strong>：</p><ul><li><code>某城市的订单量占比 = 该城市订单数 / 总订单数</code></li><li>适用于 <code>类别较多</code>（如 <code>城市名称</code>）。</li></ul></li><li><p><strong>目标编码（Target Encoding）</strong>：</p><ul><li><code>类别 A 的目标均值 = 该类别的目标变量均值</code></li><li>适用于 <code>类别变量与目标变量有相关性</code> 的情况，如 <code>职业 vs 收入</code>。</li></ul></li></ul><p>分组统计特征的核心逻辑是：<em>如果我们能知道某个群体的平均表现，那么预测个体的成绩时可以参考这个群体的水平。</em></p><ul><li><strong>均值、方差、标准差、中位数</strong>：同一课程的学习成绩、平均学习时长</li><li><strong>最大值、最小值、四分位数</strong>：用于分析数据的分布情况</li><li><strong>计数特征</strong>：如某用户在某一时间段内的购买次数。</li></ul><p>定义分组变量：<code>[&#39;gender&#39;, &#39;course&#39;]</code> 表示“同一性别同一课程”的学生群体</p><p>计算分组统计量：计算该分组下的目标变量统计值：<code>平均成绩(mean)</code>, <code>中位数(median)</code>, <code>标准差(std)</code>, <code>最大成绩(max)</code>, <code>最小成绩(min)</code></p><p>特征替换：将原始的分类特征或数值特征替换为这些统计量</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>1、特征交叉</span></span>
<span class="line"><span></span></span>
<span class="line"><span>df[&#39;gender_course&#39;] = df[&#39;gender&#39;].astype(str) + &#39;_&#39; + df[&#39;course&#39;].astype(str)</span></span>
<span class="line"><span></span></span>
<span class="line"><span>2. 目标编码（仅在训练集上计算）</span></span>
<span class="line"><span></span></span>
<span class="line"><span>train_stats_dict = df.groupby(&#39;gender_course&#39;)[&#39;exam_score&#39;].mean().to_dict()</span></span>
<span class="line"><span></span></span>
<span class="line"><span>df[&#39;target_mean_gender_course&#39;] = df[&#39;gender_course&#39;].map(train_stats_dict)</span></span></code></pre></div><h4 id="_2-词向量编码" tabindex="-1"><strong>(2) 词向量编码</strong> <a class="header-anchor" href="#_2-词向量编码" aria-label="Permalink to &quot;**(2) 词向量编码**&quot;">​</a></h4><ul><li><strong>TF-IDF</strong>：常用于文本数据，计算词频和逆文档频率。</li><li><strong>Word 2 Vec / BERT</strong>：将文本转换成词向量，提高语义理解能力。</li></ul>`,10),l=[c];function d(t,i,p,r,_,g){return n(),s("div",null,l)}const h=e(a,[["render",d]]);export{m as __pageData,h as default};
