import{_ as a,c as s,o as n,a4 as e}from"./chunks/framework.4aTu-Nia.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/02 技术/机器学习/特征降维.md","filePath":"docs/02 技术/机器学习/特征降维.md"}'),p={name:"docs/02 技术/机器学习/特征降维.md"},t=e(`<h2 id="为什么需要特征降维" tabindex="-1">为什么需要特征降维？ <a class="header-anchor" href="#为什么需要特征降维" aria-label="Permalink to &quot;为什么需要特征降维？&quot;">​</a></h2><p>对于某些机器学习方法，训练时长会随着特征数量的增加呈指数级增长；在这种情况下，我们可以使用特征降维方法，减少计算复杂性、缓解维度灾难、并可能去除冗余和噪声，从而提升模型的训练效率和泛化能力**</p><h2 id="如何实现特征降维" tabindex="-1">如何实现特征降维 <a class="header-anchor" href="#如何实现特征降维" aria-label="Permalink to &quot;如何实现特征降维&quot;">​</a></h2><p>输入：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>import pandas as pd</span></span>
<span class="line"><span>from sklearn.decomposition import PCA</span></span>
<span class="line"><span></span></span>
<span class="line"><span>df = pd.DataFrame([[2,4,6,8], [4,8,12,16]])</span></span>
<span class="line"><span>display(df)</span></span></code></pre></div><p>降维处理：</p><blockquote><p>将四维 pandas 降为二维，每个特征都是原始特征的线性组合</p></blockquote><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>import pandas as pd</span></span>
<span class="line"><span>from sklearn.decomposition import PCA</span></span>
<span class="line"><span>dr = PCA(n_components=2)</span></span>
<span class="line"><span>reduced_df = dr.fit_transform(df)</span></span>
<span class="line"><span>print(reduced_df) # 输出 array 类型</span></span></code></pre></div><p>输出：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>array([[ 5.47722558e+00,  6.66133815e-16],</span></span>
<span class="line"><span>       [-5.47722558e+00,  6.66133815e-16]])</span></span></code></pre></div><h2 id="局限性" tabindex="-1">局限性 <a class="header-anchor" href="#局限性" aria-label="Permalink to &quot;局限性&quot;">​</a></h2><p><strong>PCA（主成分分析）要求其输入特征必须是数值型的。这是因为PCA的数学基础是基于协方差矩阵或相关系数矩阵</strong>的计算。这些矩阵的计算涉及到特征之间的加减乘除等线性代数运算，而这些运算只能应用于数值数据</p><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><p><a href="https://neptune.ai/blog/feature-engineering-tools" target="_blank" rel="noreferrer">https://neptune.ai/blog/feature-engineering-tools</a></p>`,14),o=[t];function i(l,r,c,d,h,u){return n(),s("div",null,o)}const g=a(p,[["render",i]]);export{m as __pageData,g as default};
