import{_ as e,c as a,o as t,a4 as l}from"./chunks/framework.4aTu-Nia.js";const b=JSON.parse('{"title":"item2","description":"","frontmatter":{},"headers":[],"relativePath":"docs/02 技术/编程开发/item2.md","filePath":"docs/02 技术/编程开发/item2.md"}'),r={name:"docs/02 技术/编程开发/item2.md"},i=l(`<h1 id="item2" tabindex="-1">item2 <a class="header-anchor" href="#item2" aria-label="Permalink to &quot;item2&quot;">​</a></h1><h2 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h2><p>Iterm2 是苹果系统增加的命令行终端工具，定制化很强使用方便，并且外观也好看，包含拆分窗格等功能</p><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>先安装 on-my-zsh, 直接从官网下载安装即可：<a href="https://iterm2.com/" target="_blank" rel="noreferrer">https://iterm2.com/</a></p><h2 id="配置" tabindex="-1">配置 <a class="header-anchor" href="#配置" aria-label="Permalink to &quot;配置&quot;">​</a></h2><p>启动时固定全屏</p><p><img src="https://fastly.jsdelivr.net/gh/caijinlin/imgcdn/image-20230603181532485.png" alt=""></p><h3 id="配色" tabindex="-1">配色 <a class="header-anchor" href="#配色" aria-label="Permalink to &quot;配色&quot;">​</a></h3><p>主题：<a href="https://raw.githubusercontent.com/mbadolato/iTerm2-Color-Schemes/master/schemes/BlulocoDark.itermcolors" target="_blank" rel="noreferrer"><strong>BlulocoDark</strong></a></p><p>首先从<a href="https://iterm2colorschemes.com/" target="_blank" rel="noreferrer">https://iterm2colorschemes.com/</a> 网站提供了丰富的风格包以供下载，然后直接打开即可导入风格包到 iterm2，推荐 BlulocoDark</p><p><img src="https://fastly.jsdelivr.net/gh/caijinlin/imgcdn/image-20230618231722404.png" alt=""></p><h3 id="字体图标" tabindex="-1">字体图标 <a class="header-anchor" href="#字体图标" aria-label="Permalink to &quot;字体图标&quot;">​</a></h3><p>安装字体，有些风格的主题显示图标时就可以正常显示了</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>cd</span></span>
<span class="line"><span>git clone git@github.com:powerline/fonts.git --depth=1</span></span>
<span class="line"><span>cd fonts</span></span>
<span class="line"><span>./install.sh</span></span>
<span class="line"><span>cd ..</span></span>
<span class="line"><span>rm -rf fonts</span></span></code></pre></div><p>安装完字体，在 item2 终端进行设置</p><p><img src="https://fastly.jsdelivr.net/gh/caijinlin/imgcdn/tmux_setting.png" alt=""></p><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><h3 id="查找" tabindex="-1">查找 <a class="header-anchor" href="#查找" aria-label="Permalink to &quot;查找&quot;">​</a></h3><ul><li><code>ctrl + p</code> 上一条命令</li><li><code>ctrl + n</code> 下一条命令</li><li><code>ctrl + r</code> 查找历史命令</li><li><code>ctrl + i</code> 包含当前关键字的上一个命令</li><li><code>command + ;</code> 自动列出之前输入过的类似命令</li></ul><h3 id="移动" tabindex="-1">移动 <a class="header-anchor" href="#移动" aria-label="Permalink to &quot;移动&quot;">​</a></h3><ul><li><code>ctrl + a/ctrl + e</code> 移动到行首/行尾</li><li><code>ctrl + f/ctrl +b </code> 前后移动字符</li><li><code>option + → | ←</code> 前后移动单词（<a href="https://blog.csdn.net/skyyws/article/details/78480132%EF%BC%89" target="_blank" rel="noreferrer">https://blog.csdn.net/skyyws/article/details/78480132）</a></li></ul><h3 id="删除" tabindex="-1">删除 <a class="header-anchor" href="#删除" aria-label="Permalink to &quot;删除&quot;">​</a></h3><ul><li><code>ctrl + d/ctrl + h</code> 删除当前光标、当前光标之前的字符</li><li><code>ctrl + w</code> 删除光标之前的单词</li><li><code>ctrl + k</code> 删除到文本末尾</li><li><code>ctrl + u</code> 清除当前行</li></ul><h3 id="替换" tabindex="-1">替换 <a class="header-anchor" href="#替换" aria-label="Permalink to &quot;替换&quot;">​</a></h3><ul><li><code>ctrl + t</code> 交换光标处文本</li></ul><h3 id="撤销" tabindex="-1">撤销 <a class="header-anchor" href="#撤销" aria-label="Permalink to &quot;撤销&quot;">​</a></h3><ul><li><code>ctrl + -</code> 撤销</li></ul><h3 id="分屏" tabindex="-1">分屏 <a class="header-anchor" href="#分屏" aria-label="Permalink to &quot;分屏&quot;">​</a></h3><ul><li><code>command + d</code> 上下分屏</li><li><code>command + shift + d</code> 左右分屏</li><li><code>command + ]</code> 切换分屏</li><li><code>command + w</code> 关闭分屏</li></ul><h3 id="标签" tabindex="-1">标签 <a class="header-anchor" href="#标签" aria-label="Permalink to &quot;标签&quot;">​</a></h3><ul><li><code>command + t</code> 新建标签</li><li><code>ctrl + tab</code> 切换标签</li><li><code>command + w</code> 关闭标签</li></ul><h2 id="插件" tabindex="-1">插件 <a class="header-anchor" href="#插件" aria-label="Permalink to &quot;插件&quot;">​</a></h2><p><a href="https://github.com/muchuang1024/dotfiles/blob/master/item2/iterm2-recv-zmodem.sh" target="_blank" rel="noreferrer">iterm2-recv-zmodem.sh</a></p><p><a href="https://github.com/muchuang1024/dotfiles/blob/master/item2/iterm2-send-zmodem.sh" target="_blank" rel="noreferrer">iterm2-send-zmodem.sh</a></p>`,35),o=[i];function c(s,n,d,h,m,p){return t(),a("div",null,o)}const f=e(r,[["render",c]]);export{b as __pageData,f as default};
