import{_ as a,c as t,o as r,a4 as o}from"./chunks/framework.4aTu-Nia.js";const q=JSON.parse('{"title":"一、硬约束好处","description":"","frontmatter":{},"headers":[],"relativePath":"docs/02 技术/运筹优化/模型建模/建模技巧/硬约束与软约束对比.md","filePath":"docs/02 技术/运筹优化/模型建模/建模技巧/硬约束与软约束对比.md"}'),n={name:"docs/02 技术/运筹优化/模型建模/建模技巧/硬约束与软约束对比.md"},l=o('<h1 id="一、硬约束好处" tabindex="-1">一、硬约束好处 <a class="header-anchor" href="#一、硬约束好处" aria-label="Permalink to &quot;一、硬约束好处&quot;">​</a></h1><h3 id="_1-保证解的可行性" tabindex="-1"><strong>1. 保证解的可行性</strong>` <a class="header-anchor" href="#_1-保证解的可行性" aria-label="Permalink to &quot;**1. 保证解的可行性**`&quot;">​</a></h3><ul><li><strong>确定性</strong>：确保任何可行解都满足平衡要求</li><li><strong>业务保障</strong>：避免出现极端不平衡的分配（如一个司机10辆车，另一个只有1辆）</li><li><strong>公平性</strong>：保证司机之间的工作量相对均衡</li></ul><h3 id="_2-简化求解复杂度" tabindex="-1"><strong>2. 简化求解复杂度</strong> <a class="header-anchor" href="#_2-简化求解复杂度" aria-label="Permalink to &quot;**2. 简化求解复杂度**&quot;">​</a></h3><ul><li><strong>减少搜索空间</strong>：硬约束直接排除了不平衡的解</li><li><strong>提高求解效率</strong>：不需要在大量不平衡解中寻找&quot;最优&quot;的平衡解</li><li><strong>避免局部最优</strong>：软约束可能导致算法陷入看似优化但实际上不平衡的解</li></ul><h3 id="_3-业务需求匹配" tabindex="-1"><strong>3. 业务需求匹配</strong> <a class="header-anchor" href="#_3-业务需求匹配" aria-label="Permalink to &quot;**3. 业务需求匹配**&quot;">​</a></h3><ul><li><strong>运营要求</strong>：实际业务中，司机工作量的平衡是硬性要求</li><li><strong>用户体验</strong>：避免某些司机过载而其他司机闲置</li><li><strong>管理便利</strong>：便于制定统一的考核和激励机制</li></ul><h1 id="二、软约束缺点" tabindex="-1">二、软约束缺点 <a class="header-anchor" href="#二、软约束缺点" aria-label="Permalink to &quot;二、软约束缺点&quot;">​</a></h1><h3 id="软约束的潜在问题" tabindex="-1"><strong>软约束的潜在问题</strong> <a class="header-anchor" href="#软约束的潜在问题" aria-label="Permalink to &quot;**软约束的潜在问题**&quot;">​</a></h3><h4 id="_1-目标函数冲突" tabindex="-1"><strong>1. 目标函数冲突</strong> <a class="header-anchor" href="#_1-目标函数冲突" aria-label="Permalink to &quot;**1. 目标函数冲突**&quot;">​</a></h4><ul><li><strong>权重难调</strong>：λ 的选择很困难，太小不起作用，太大影响距离优化</li><li><strong>Pareto最优</strong>：需要在距离和平衡之间做权衡，可能两个目标都得不到满意结果</li></ul><h4 id="_2-解的质量不确定" tabindex="-1"><strong>2. 解的质量不确定</strong> <a class="header-anchor" href="#_2-解的质量不确定" aria-label="Permalink to &quot;**2. 解的质量不确定**&quot;">​</a></h4><ul><li><strong>可能得到次优解</strong>：算法可能找到一个距离较短但很不平衡的解</li><li><strong>缺乏保障</strong>：无法确保最终的平衡程度满足业务要求</li></ul><h4 id="_3-求解复杂度增加" tabindex="-1"><strong>3. 求解复杂度增加</strong> <a class="header-anchor" href="#_3-求解复杂度增加" aria-label="Permalink to &quot;**3. 求解复杂度增加**&quot;">​</a></h4><ul><li><strong>多目标优化</strong>：需要处理目标之间的优先级和权重</li><li><strong>收敛困难</strong>：可能在不同目标之间来回震荡，难以收敛</li></ul>',15),e=[l];function s(i,h,_,g,d,c){return r(),t("div",null,e)}const b=a(n,[["render",s]]);export{q as __pageData,b as default};
