import{_ as l,c as i,o as a,a4 as e}from"./chunks/framework.4aTu-Nia.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/02 技术/运筹优化/运筹优化知识地图.md","filePath":"docs/02 技术/运筹优化/运筹优化知识地图.md"}'),o={name:"docs/02 技术/运筹优化/运筹优化知识地图.md"},r=e('<h2 id="_1-基础理论" tabindex="-1">1. 基础理论 <a class="header-anchor" href="#_1-基础理论" aria-label="Permalink to &quot;1. 基础理论&quot;">​</a></h2><ul><li>线性规划（LP） <ul><li>[[线性规划优化方法]]</li><li>[[分段线性非凸函数转换为线性优化]]</li><li>[[线性规划对偶问题]]</li><li>[[约束优化]]</li></ul></li><li>整数规划（IP/MIP）</li><li>凸优化基础</li><li>图论基础（最短路、最大流）</li></ul><h2 id="_2-求解方法" tabindex="-1">2. 求解方法 <a class="header-anchor" href="#_2-求解方法" aria-label="Permalink to &quot;2. 求解方法&quot;">​</a></h2><h3 id="精确算法" tabindex="-1">精确算法 <a class="header-anchor" href="#精确算法" aria-label="Permalink to &quot;精确算法&quot;">​</a></h3><ul><li>单纯形法</li><li>内点法</li><li>分支定界（Branch and Bound）</li><li>分支切割（Branch and Cut）</li></ul><h3 id="启发式-元启发式" tabindex="-1">启发式 / 元启发式 <a class="header-anchor" href="#启发式-元启发式" aria-label="Permalink to &quot;启发式 / 元启发式&quot;">​</a></h3><ul><li>贪心算法</li><li>局部搜索</li><li>模拟退火</li><li>遗传算法</li><li>禁忌搜索</li></ul><h3 id="大规模优化技术" tabindex="-1">大规模优化技术 <a class="header-anchor" href="#大规模优化技术" aria-label="Permalink to &quot;大规模优化技术&quot;">​</a></h3><ul><li>列生成（Column Generation） <ul><li>[[列生成算法]]</li></ul></li><li>拉格朗日松弛</li><li>Benders 分解</li><li>双向标号算法（路径生成） <ul><li>[[双向标号算法]]</li></ul></li></ul><h2 id="_3-典型模型" tabindex="-1">3. 典型模型 <a class="header-anchor" href="#_3-典型模型" aria-label="Permalink to &quot;3. 典型模型&quot;">​</a></h2><ul><li><p>运输问题（Transportation Problem）</p></li><li><p>指派问题（Assignment Problem）</p></li><li><p>网络流（Network Flow）</p></li><li><p>背包问题（Knapsack）</p></li><li><p>VRP （车辆路径规划）</p></li><li><p>调度问题（Scheduling）</p></li><li><p>选址问题（Facility Location）</p></li></ul><h2 id="_4-在城市运维与调度中的应用" tabindex="-1">4. 在城市运维与调度中的应用 <a class="header-anchor" href="#_4-在城市运维与调度中的应用" aria-label="Permalink to &quot;4. 在城市运维与调度中的应用&quot;">​</a></h2><ul><li><p>单车调度（再平衡）</p></li><li><p>换电站派单</p></li><li><p>城市街道车辆分配</p></li><li><p>路径生成与工单构建（列生成）</p></li><li><p>司机路径优化（capacity + sequence）</p></li><li><p>多装多卸路径结构</p></li></ul><h2 id="_5-模型构建流程" tabindex="-1">5. 模型构建流程 <a class="header-anchor" href="#_5-模型构建流程" aria-label="Permalink to &quot;5. 模型构建流程&quot;">​</a></h2><ol><li><p>明确决策变量</p></li><li><p>构造目标函数（收益最大化 / 成本最小化）</p></li><li><p>添加约束条件</p></li><li><p>数据准备：需求、容量、距离、成本</p></li><li><p>使用求解器（如 Gurobi）求解</p></li><li><p>对结果做回测与敏感性分析</p></li></ol><h2 id="_6-常用工具与生态" tabindex="-1">6. 常用工具与生态 <a class="header-anchor" href="#_6-常用工具与生态" aria-label="Permalink to &quot;6. 常用工具与生态&quot;">​</a></h2><ul><li><p>求解器：Gurobi / CPLEX / OR-Tools</p></li><li><p>编程语言：Python、Java</p></li><li><p>可视化：networkx、folium</p></li><li><p>文献资源：Operations Research、Math Programming</p></li></ul><h2 id="_7-学习路径建议" tabindex="-1">7. 学习路径建议 <a class="header-anchor" href="#_7-学习路径建议" aria-label="Permalink to &quot;7. 学习路径建议&quot;">​</a></h2><ul><li><p>先理解线性规划与整数规划</p></li><li><p>再学习网络优化与图论</p></li><li><p>学习大规模优化技术（列生成、分解方法）</p></li><li><p>最后结合实际业务建模（调度/预测/路径）</p></li></ul><h2 id="_8-延伸主题" tabindex="-1">8. 延伸主题 <a class="header-anchor" href="#_8-延伸主题" aria-label="Permalink to &quot;8. 延伸主题&quot;">​</a></h2><ul><li><p>贝叶斯优化（超参调优）</p></li><li><p>决策与预测联动（Predict + Optimize）</p></li><li><p>强化学习在调度中的应用</p></li></ul>',21),t=[r];function p(n,h,u,c,d,s){return a(),i("div",null,t)}const b=l(o,[["render",p]]);export{m as __pageData,b as default};
